import React,{useState} from 'react';

import Button from './Button';
import './AddTask.css';

const AddTask = ({hangleTaskAddition, edit, setEdit}) =>{

    const[inputData, setinputData] = useState('');
  
    
    const handleInputChange = (e) =>{
            setinputData(e.target.value);
    }

  
    const handdleAddTaskClick = () =>{
            hangleTaskAddition(inputData);
            setinputData('');

    }

     const handleEditTaskClick = () => {
        setEdit((edit) => {return edit});
    }
   
  
    return(
        <div className='add-task-container'>
                <input className="add-task-input" type="text" value={inputData} onChange={handleInputChange}/>
            <div className="add-task-button-container">
                {edit ? <Button  onClick={handleEditTaskClick}>Editar</Button> : <Button  onClick={handdleAddTaskClick}>Adicionar</Button>}
                
            </div>
        </div>
    )
}

export default AddTask;
