import React from 'react';
import AddTask from './AddTask';
import Task from './Task';

const Tasks = ({tasks}) =>{
    return(
        <>
            <AddTask task={tasks} setTask={setTask}/>
            {tasks.map(task => <Task task={task}/>)}
        </>
    )
}

export default Tasks;
