import React, {useState} from 'react';

import Tasks from './components/Tasks';
import AddTask from './components/AddTask';
import './App.css';


const App = () => {
    const [task,setTask] = useState([
      {
        id: "1",
        title: "Estudar Programação",
        completed: false,
      },
      {
        id: "2",
        title: "Ler Livros",
        completed: true,
      }
    ]);

    const hangleTaskAddition = (taskTittle)  =>{
        const newTasks = [...tasks,{
          title: taskTittle,
          id:Math.random(10),
          completed:false,
        }]

        setTask(newTasks);
    }

  return(
    <>
      <div className="container">
        <AddTask hangleTaskAddition={hangleTaskAddition}/>
        <Tasks tasks={task} setTask={setTask} />
      </div>
    </>
  )
}

export default App;
