import React from 'react';
import './Task.css';
const Task = ({task,handdleTaskClick}) =>{
    return(
        <>
           <div className="task-container" 
           style={task.completed ? {borderLeft: "6px solid chartreuse", textDecoration: 'underline'} : {}}>
                <div className='task-title' onClick={() => handdleTaskClick(task.id)}>
                    {task.title}
                </div>
           </div>
        </>
    )
}

export default Task;
