    const [task,setTask] = useState([
      {
        id: "1",
        title: "Estudar Programação",
        completed: false,
      },
      {
        id: "2",
        title: "Ler Livros",
        completed: true,
      }
    ]);

    const handdleTaskClick = (taskId) =>{
      const newTasks = task.map(task => {
        if(task.id == taskId) return{... task, completed: !task.completed}

        return task;
      })

      setTask(newTasks);
    }

    const hangleTaskAddition = (taskTittle)  =>{
        const newTasks = [...task,{
          title: taskTittle,
          id: uuidv4(),
          completed:false,
        }]

        setTask(newTasks);
    }

    const handdleRemoveTask = (taskId) =>{
        const position = task.findIndex(x=>x.id == taskId);
        const newTask = [...task];
        newTask.splice()
    }

  return(
    <>
      <div className="container">
        <AddTask hangleTaskAddition={hangleTaskAddition}/>
        <Tasks tasks={task} handdleTaskClick={handdleTaskClick}/>
      </div>
    </>
  )
}

export default App;
