import React from 'react';

import Task from './Task';

const Tasks = ({tasks, handdleTaskClick,handdleRemoveTask, handleTaskEdit}) =>{
    return(
        <>
           
            {tasks.map(task => <Task key={tasks.id} task={task} handdleTaskClick={handdleTaskClick} handdleRemoveTask={handdleRemoveTask} handleTaskEdit={handleTaskEdit}/>)}
        </>
    )
}

export default Tasks;
