import React from 'react';
import {CgClose} from 'react-icons/cg';
import './Task.css';
const Task = ({task,handdleTaskClick,handdleRemoveTask}) =>{
    return(
        <>
           <div className="task-container" 
           style={task.completed ? {borderLeft: "6px solid chartreuse", textDecoration: 'line-through'} : {}}>
                <div className='task-title' onClick={() => handdleTaskClick(task.id)}>
                    {task.title}
                </div>

                <div className="buttons-container">
                    <button style={{cursor:""}} className="remove-task-button" onClick={() => handdleRemoveTask(task.id)}><CgClose/></button>
                </div>
           </div>
        </>
    )
}

export default Task;
